package armo_builtins


deny[msga] {
	workload := input[_]
	workload_kinds := {"Deployment","ReplicaSet","DaemonSet","StatefulSet","Job", "Pod", "CronJob"}
	workload_kinds[workload.kind]

	networkpolicies := [networkpolicy | networkpolicy = input[_]; networkpolicy.kind == "NetworkPolicy"]
	not connected_to_any_network_policy(workload, networkpolicies)

	msga := {
		"alertMessage": sprintf("%v: no networkpolicy configured in labels", [workload.metadata.name]),
		"packagename": "armo_builtins",
		"failedPaths": [],
		"fixPaths":[],
		"alertObject": {
			"k8sApiObjects": [workload]
		}
	}
}


connected_to_any_network_policy(workload, networkpolicies){
	connected_to_network_policy(workload, networkpolicies[_])
}

# connected_to_network_policy returns true if the workload is connected to the networkpolicy
connected_to_network_policy(wl, networkpolicy){
	workload_kinds := {"Deployment","ReplicaSet","DaemonSet","StatefulSet","Job"}
	workload_kinds[wl.kind]
	is_same_namespace(networkpolicy.metadata, wl.metadata)
	count(networkpolicy.spec.podSelector) > 0
    count({x | networkpolicy.spec.podSelector.matchLabels[x] == wl.spec.template.metadata.labels[x]}) == count(networkpolicy.spec.podSelector.matchLabels)
}

# connected_to_network_policy returns true if the workload is connected to the networkpolicy
connected_to_network_policy(wl, networkpolicy){
	wl.kind == "Pod"
	is_same_namespace(networkpolicy.metadata, wl.metadata)
    count(networkpolicy.spec.podSelector) > 0
    count({x | networkpolicy.spec.podSelector.matchLabels[x] == wl.metadata.labels[x]}) == count(networkpolicy.spec.podSelector.matchLabels)
}

# connected_to_network_policy returns true if the workload is connected to the networkpolicy
connected_to_network_policy(wl, networkpolicy){
	wl.kind == "CronJob"
	is_same_namespace(networkpolicy.metadata, wl.metadata)
	count(networkpolicy.spec.podSelector) > 0
    count({x | networkpolicy.spec.podSelector.matchLabels[x] == wl.spec.jobTemplate.spec.template.metadata.labels[x]}) == count(networkpolicy.spec.podSelector.matchLabels)
}

# connected_to_network_policy returns true if the NetworkPolicy has no podSelector.
# if the NetworkPolicy has no podSelector, it is applied to all workloads in the namespace of the NetworkPolicy
connected_to_network_policy(wl, networkpolicy){
	is_same_namespace(networkpolicy.metadata, wl.metadata)
    count(networkpolicy.spec.podSelector) == 0
}


is_same_namespace(metadata1, metadata2) {
	metadata1.namespace == metadata2.namespace
}

is_same_namespace(metadata1, metadata2) {
	not metadata1.namespace
	not metadata2.namespace
}

is_same_namespace(metadata1, metadata2) {
	not metadata2.namespace
	metadata1.namespace == "default"
}

is_same_namespace(metadata1, metadata2) {
	not metadata1.namespace
	metadata2.namespace == "default"
}

package armo_builtins

import future.keywords.every
import future.keywords.in

# =============================== GKE ===============================
# Check if audit logs is enabled for GKE
deny[msga] {
	cluster_config := input[_]
	cluster_config.apiVersion == "container.googleapis.com/v1"
	cluster_config.kind == "ClusterDescribe"
	cluster_config.metadata.provider == "gke"
	config := cluster_config.data

	# If enableComponents is empty, it will disable logging
	# https://cloud.google.com/kubernetes-engine/docs/reference/rest/v1beta1/projects.locations.clusters#loggingcomponentconfig
	is_logging_disabled(config)
	msga := {
		"alertMessage": "audit logs is disabled",
		"alertScore": 3,
		"packagename": "armo_builtins",
		"failedPaths": [],
		"fixPaths": [],
		"fixCommand": "",
		"alertObject": {
			"k8sApiObjects": [],
			"externalObjects": cluster_config,
		},
	}
}

is_logging_disabled(cluster_config) {
	not cluster_config.logging_config.component_config.enable_components
}

is_logging_disabled(cluster_config) {
	cluster_config.logging_config.component_config.enable_components
	count(cluster_config.logging_config.component_config.enable_components) == 0
}

# =============================== EKS ===============================
# Check if audit logs is enabled for EKS
deny[msga] {
	cluster_config := input[_]
	cluster_config.apiVersion == "eks.amazonaws.com/v1"
	cluster_config.kind == "ClusterDescribe"
	cluster_config.metadata.provider == "eks"
	config := cluster_config.data

	# logSetup is an object representing the enabled or disabled Kubernetes control plane logs for your cluster.
	# types - available cluster control plane log types
	# https://docs.aws.amazon.com/eks/latest/APIReference/API_LogSetup.html
	logging_types := {"api", "audit", "authenticator", "controllerManager", "scheduler"}
	logSetups = config.Cluster.Logging.ClusterLogging
	not all_auditlogs_enabled(logSetups, logging_types)

	msga := {
		"alertMessage": "audit logs is disabled",
		"alertScore": 3,
		"packagename": "armo_builtins",
		"failedPaths": [],
		"fixCommand": "aws eks update-cluster-config --region '${REGION_CODE}' --name '${CLUSTER_NAME}' --logging '{'clusterLogging':[{'types':['api','audit','authenticator','controllerManager','scheduler'],'enabled':true}]}'",
		"fixPaths": [],
		"alertObject": {
			"k8sApiObjects": [],
			"externalObjects": cluster_config,
		},
	}
}

all_auditlogs_enabled(logSetups, types) {
	every type in types {
		auditlogs_enabled(logSetups, type)
	}
}

auditlogs_enabled(logSetups, type) {
	logSetup := logSetups[_]
	logSetup.Enabled == true
	type in logSetup.Types
}

package armo_builtins

# Checks if Ingress is connected to a service and a workload to expose something
deny[msga] {
	ingress := input[_]
	ingress.kind == "Ingress"

	# Check if ingress has TLS enabled
	not ingress.spec.tls

	msga := {
		"alertMessage": sprintf("Ingress '%v' has not TLS definition", [ingress.metadata.name]),
		"packagename": "armo_builtins",
		"failedPaths": [],
		"fixPaths": [{
        "path": "spec.tls",
        "value": "<your-tls-definition>"
        }],
		"alertScore": 7,
		"alertObject": {"k8sApiObjects": [ingress]}
	}
}
